module ApplicationHelper
  # Heroicon helper - renders outline icons by default
  # Usage: <%= heroicon "chevron-left", class: "h-5 w-5" %>
  # Usage: <%= heroicon "plus", size: "6", class: "text-blue-500" %>
  def heroicon(name, **options)
    size = options.delete(:size) || "5"
    css_class = options.delete(:class) || ""
    css_class = "h-#{size} w-#{size} #{css_class}".strip

    svg_attrs = {
      xmlns: "http://www.w3.org/2000/svg",
      fill: "none",
      viewBox: "0 0 24 24",
      "stroke-width": "1.5",
      stroke: "currentColor",
      class: css_class
    }.merge(options)

    content_tag(:svg, svg_attrs) do
      heroicon_path(name)
    end
  end

